import React, {Component} from 'react';
import Die from './Die';
import './RollDice.css'

const faces = ['fas fa-dice-one', 'fas fa-dice-two', 'fas fa-dice-three',
                    'fas fa-dice-four','fas fa-dice-five', 'fas fa-dice-six'];


class RollDice extends Component{


    constructor(props){
        super(props);
        this.state = {
            die1 : 'fas fa-dice-one' ,
            die2 : 'fas fa-dice-one'
        }

    }

    genRandom(){
        let num = Math.floor((Math.random())*6);
        return faces[num];
    }

    rollDice(){
        let newDie1 = ''+ genRandom();
        let newDie2 = ''+ genRandom();

        this.setState({die1: newDie1, die2: newDie2});
    }

    render(){
        return <div>

            <div className='RollDice-flex'>

                {/* <span className='RollDice-die animated wobble'><Die rolled={this.genRandom()}/></span> */}
                {/* defining two dice */}
                <Die value={this.state.die1}/>
                <Die value={this.state.die2}/>




            </div>

            <button onClick={this.rollDice}>Roll</button>



        </div>
    }
}

export default RollDice;
